import { useEffect, useState } from 'react';
import './App.css';
import * as React from 'react';
import Box from '@mui/material/Box';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import Divider from '@mui/material/Divider';

function App() {
  const [input, setInput] = useState('')
  const [pk, setPk] = useState(0)
  const [todo, setTodo] = useState([])

  useEffect(() =>{
    if(JSON.parse(localStorage.getItem('todo'))!==null){
      let todoJson = JSON.parse(localStorage.getItem('todo'))
      setTodo(todoJson)
      let max = 0
      todoJson.map(item => {
        if(item.id > max){
          max = item.id
        }
      })
      setPk(max+1)
    }
  },[]);

  const inputAddToTodo = () =>{
      setTodo([...todo, {id: pk, todos: input}])
      setPk(pk+1)

  }

  const removeFromTodo = (id) =>{
    setTodo(val =>
      val.filter(e => {
        return e.id !== id;
      }),
    );
  }

  const save = () => {
    var data = JSON.stringify(todo);
    localStorage.setItem('todo', data);
  }

  return (
    <div className="App">
      <h1>Todo</h1>
      <input type="text"  onChange={(e) => {setInput(e.target.value)}}/>
      <br></br>
      <button onClick={()=>inputAddToTodo()}>Submit</button>
      <h1>Tasks</h1>
      {todo.map((val) => {
        <List>
        <ListItem disablePadding>
          <ListItemButton>
          <ListItemText primary="Inbox" />
            <ListItemIcon>
              icon
            </ListItemIcon>
          </ListItemButton>
        </ListItem>
      </List>
        return <div>
            <h2>{val.todos} &nbsp;&nbsp; <button onClick={()=>removeFromTodo(val.id)}>Remove</button></h2>
        </div>
      })}

      <button onClick={()=> save()}>Save</button>

      <Box sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}>
      <nav aria-label="main mailbox folders">

      </nav>
    </Box>


    </div>
  );
}

export default App;
