import { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [input, setInput] = useState('')
  const [pk, setPk] = useState(0)
  const [todo, setTodo] = useState([])
  useEffect(() =>{localStorage.setItem('todo', null)
    if(JSON.parse(localStorage.getItem('todo'))!==null){
      setTodo(JSON.parse(localStorage.getItem('todo')))
    }
  },[todo]);

  const inputAddToTodo = () =>{
      setTodo([...todo, {id: pk, todos: input}])
      setPk(pk+1)
  }

  const removeFromTodo = (id) =>{
    setTodo(val =>
      val.filter(e => {
        return e.id !== id;
      }),
    );
  }

  const save = () => {
    localStorage.setItem('todo', (localStorage.getItem('todo')+JSON.stringify(todo)));
  }


  console.log(todo)
  return (
    <div className="App">
      <h1>Todo</h1>
      <input type="text"  onChange={(e) => {setInput(e.target.value)}}/>
      <br></br>
      <button onClick={()=>inputAddToTodo()}>Submit</button>
      <h1>Tasks</h1>
      {todo.map((val) => {
        return <div>
            <h2>{val.todos} &nbsp;&nbsp; <button onClick={()=>removeFromTodo(val.id)}>Remove</button></h2>
        </div>
      })}

      <button onClick={()=> save()}>Save</button>


    </div>
  );
}

export default App;
